package examen_3EVA_Ejercicio1;

public class Letras {

	public String letra;
	public int linea;

	public Letras() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Letras(String letra, int linea) {
		super();
		this.letra = letra;
		this.linea = linea;
	}

	public String getLetra() {
		return letra;
	}

	public void setLetra(String letra) {
		this.letra = letra;
	}

	public int getLinea() {
		return linea;
	}

	public void setLinea(int linea) {
		this.linea = linea;
	}

}
